import React from "react";
import { Text, View, Image } from "react-native";
export const FoodDetail = ({route, navigation}) => {
    const {foodData} = route.params
    return(
        <View style={{
            flex : 1,
            alignItems : 'center',
            padding : 10
        }}
        >
            <View style={{
                width : '100%'
            }}>
                <Image
                    style={{width : '100%', height : 250, borderRadius : 10}}
                    source={{uri: foodData.image}}
                />
            </View>

            <View style={{}}></View>
        </View>
    )
}
