import React, { useState } from 'react';
import { TouchableOpacity } from 'react-native';
import { StyleSheet, Text, View } from 'react-native';
import clock from "./countdown"

export default function PlayACourse({ route, navigation }) {
  const { postureData } = route.params
  // const timeConvert = (time) => {
  //   const min = Math.floor(time / 60)
  //   const sec = time % 60
  // }
  const Stack = createNativeStackNavigator();
  const [run, setRun] = useState(true);
  const [count, setCount] = useState(0);
  const [first, setfirst] = useState(true);
  const pause = () => {
    if (run == true) {
      setRun(false);
    } else {
      setRun(true);
    }
    console.log(count);
    console.log(postureData[count].sec);
  }
  const goNextPose = () => {
    if (count >= postureData.length - 1) {
      console.log(count);
      console.log("finish")
    } else {
      setCount(count + 1);
    }
  }
  const handleResetClick = () => {

  }

  return (
    <View style={styles.container}>
      <Text style={{ fontSize: 40, fontWeight: '700' }}>{postureData[count].name}</Text>
      <CountDown
        id={(count).toString()}
        until={postureData[count].sec}
        size={50}
        onFinish={goNextPose}
        digitTxtStyle={{ color: 'black', fontSize: 80, fontWeight: '500' }}
        digitStyle={{ backgroundColor: '#FFF' }}
        timeToShow={['M', 'S']}
        timeLabels={{ m: null, s: null }}
        separatorStyle={{ color: 'black' }}
        showSeparator
        running={run}
      />
      <clock/>
      <TouchableOpacity style={{
        height: 50,
        width: '70%',
        backgroundColor: 'lightblue',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 5,
        marginTop: 20,
      }} onPress={pause}>
        <Text style={{
          fontSize: 22,
          fontWeight: '500'
        }}>Stop</Text>
      </TouchableOpacity>
      <TouchableOpacity style={{
        height: 50,
        width: '70%',
        backgroundColor: 'lightblue',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 5,
        marginTop: 20,
      }}>
        <Text style={{
          fontSize: 22,
          fontWeight: '500'
        }} onPress={handleResetClick}>Reset</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
