import { Ionicons } from '@expo/vector-icons';
import { StyleSheet, Text, View, ScrollView, FlatList, RefreshControl, TouchableOpacity } from 'react-native';
import { db } from '../../database/firebase';
import { useSelector } from 'react-redux';
import { collection, addDoc, onSnapshot, query, where, deleteDoc, doc, updateDoc } from 'firebase/firestore';

export default function MealScreen({ route, navigation }) {
  const [courseData, setCourseData] = useState([])
  const user = useSelector((state) => state.user_data.user)
  const mycourse = query(collection(db, 'mycourse'), where("uid", "==", user.uid))
  onSnapshot(mycourse, (snapshot) => {
    setCourseData(snapshot.docs.map(doc => doc.data()))
    setCourseDocId(snapshot.docs.map(doc => doc.id))
  })
  return (
    <View style={styles.container}>
      <View style={[styles.shadowbox, { width: "100%", padding: 10 }]}>

        <FlatList data={courseData}
          refreshControl={
            <RefreshControl
              refreshing={refreshing}
              onRefresh={() => { pullMe() }} />
          }
          renderItem={({ item, index }) =>
            <View style={{
              height: 150,
              width: '100%',
              borderWidth: 1,
              borderColor: 'lightgray',
              borderRadius: 10,
              marginBottom: 15,
              backgroundColor: item.backgroundColor
            }} key={index}>
              <View style={[styles.gridItem, { padding: 10, paddingTop: 15 }]}>
                <View style={{ flexDirection: 'row', }}>
                  <Text style={[styles.txtImg, { marginBottom: 15 }]}>{item.courseName}</Text>
                  <TouchableOpacity style={{
                    position: 'absolute',
                    right: 0,
                    top: 5
                  }}>
                    <Ionicons name='ios-trash-outline' size={20} color={'black'} />

                  </TouchableOpacity>
                </View>
                <Text style={{ fontSize: 18, fontWeight: '500' }}>{postureData.filter(posture => posture.byCourseId == item.courseId).length} Posture
                  {sumOfSec(postureData.filter(data => data.byCourseId == item.courseId).map(data => data.timeDuration))}
                </Text>
              </View>

              <View style={{ flexDirection: 'column' }}>
                <Pressable style={{
                  width: '100%',
                  height: 40,
                  backgroundColor: 'lightblue',
                  alignItems: 'center',
                  justifyContent: 'center',
                  borderRadius: 5
                }}
                  onPress={() => {
                    navigation.navigate("My Course Detail", { exerciseList: postureData, courseId: item.courseId, title: item.courseName })
                  }}
                  options={({ route }) => ({
                    title: route.params.title,
                  })}>
                  <Ionicons name='ios-enter-outline' size={20} color={'black'} />
                </Pressable>
              </View>
            </View>

          } />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 15,
  },
  render: {
    backgroundColor: "lightblue",
    width: "100%",
    height: 100,
    borderRadius: 5,
    marginBottom: 5,
    padding: 10,
    alignItems: 'center',
    flexDirection: 'row'
  },
});
