import React from "react";
import { Text, View, Image } from "react-native";
export const FoodDetail = ({route, navigation}) => {
    const {foodData} = route.params
    return(
        <View style={{
            flex : 1,
            alignItems : 'center',
            padding : 10
        }}
        >
            <View style={{
                width : '100%'
            }}>
                <Image
                    style={{width : '100%', height : 200, borderRadius : 10}}
                    source={{uri: foodData.image}}
                />
            </View>

            <View style={{
                width : '100%',
                height : 200,
                backgroundColor : 'white',
                shadowColor: '#171717',
                shadowOffset: {width: -2, height: 4},
                shadowOpacity: 0.2,
                shadowRadius: 3,
                borderRadius : 10,
                marginTop : 15,
                padding : 15
            }}>
                <Text style={{fontSize : 24, fontWeight : '700'}}>{foodData.foodTitle}</Text>
                <Text style={{fontSize : 14, fontWeight : '300'}}>{foodData.desc}</Text>
            </View>
        </View>
    )
}
