import React, { useState, useCallback, useRef } from "react";
import { StyleSheet, Text, View, TouchableOpacity, Image} from 'react-native';

export default function CourseDetailScreen({route, navigation}) {
  const {courseTitle, courseData} = route.params
  return (
    <View style={styles.container}>
      <TouchableOpacity style={{
        width : '95%',
        height : 40,
        backgroundColor : 'lightblue',
        alignItems : 'center',
        justifyContent : 'center',
        borderRadius : 5,
        marginTop : 15
      }} onPress={() => {navigation.navigate("Inner Course", {title : courseTitle, postureData : courseData.postureData})}}>
        <Text style={{fontSize : 18, fontWeight : '600'}}>Start a course</Text>
      </TouchableOpacity>
      <View style={{
                width : '100%'
            }}>
                <Image
                    style={{width : '100%', height : 200, borderRadius : 10}}
                    source={{uri: courseData.image}}
                />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
  },
});
