import React, { useState } from 'react';
import { TouchableOpacity } from 'react-native';
import { StyleSheet, Text, View } from 'react-native';
import CountDown from 'react-native-countdown-component';


export default function PlayACourse({ route, navigation }) {
  const { postureData } = route.params
  // const timeConvert = (time) => {
  //   const min = Math.floor(time / 60)
  //   const sec = time % 60
  // }

  const [run, setRun] = useState(true);
  const [count, setCount] = useState(0);
  const [first, setfirst] = useState(true);
  const pause = () => {
    if (run == true) {
      setRun(false);
    } else {
      setRun(true);
    }
    console.log(count);
    console.log(postureData[count].sec);
  }
  const goNextPose = () => {
    if (count >= postureData.length - 1) {
      console.log(count);
      console.log("finish")
    } else {
      setCount(count + 1);
    }
  }
  const handleResetClick = () => {

  }
  countdownApi: CountdownApi = null;
  state = { date: Date.now() + 10000 };

  handleStartClick = () => {
    this.countdownApi && this.countdownApi.start();
  };

  handlePauseClick = () => {
    this.countdownApi && this.countdownApi.pause();
  };

  handleResetClick = () => {
    this.setState({ date: Date.now() + 10000 });
  };

  handleUpdate = () => {
    this.forceUpdate();
  };

  setRef = (countdown: Countdown | null) => {
    if (countdown) {
      this.countdownApi = countdown.getApi();
    }
  };

  isPaused =() => {
    return !!(this.countdownApi && this.countdownApi.isPaused());
  }

  isCompleted = () => {
    return !!(this.countdownApi && this.countdownApi.isCompleted());
  }

  return (
    <View style={styles.container}>
      <Text style={{ fontSize: 40, fontWeight: '700' }}>{postureData[count].name}</Text>
      <CountDown
        id={(count).toString()}
        until={postureData[count].sec}
        onChange={(time) => {
          console.warn(time)
          if (time == 2 && count == 0) {

          }
        }}
        size={50}
        onFinish={goNextPose}
        digitTxtStyle={{ color: 'black', fontSize: 80, fontWeight: '500' }}
        digitStyle={{ backgroundColor: '#FFF' }}
        timeToShow={['M', 'S']}
        timeLabels={{ m: null, s: null }}
        separatorStyle={{ color: 'black' }}
        showSeparator
        running={run}
      />
      <Countdown
        key={this.state.date}
        ref={this.setRef}
        date={this.state.date}
        onMount={this.handleUpdate}
        onStart={this.handleUpdate}
        onPause={this.handleUpdate}
        onComplete={this.handleUpdate}
        autoStart={false}
      />
      <div>
        <button
          type="button"
          onClick={this.handleStartClick}
          disabled={!this.isPaused() || this.isCompleted()}
        >
          Start
        </button>{' '}
        <button
          type="button"
          onClick={this.handlePauseClick}
          disabled={this.isPaused() || this.isCompleted()}
        >
          Pause
        </button>{' '}
        <button type="button" onClick={this.handleResetClick}>
          Reset
        </button>
      </div>
      <TouchableOpacity style={{
        height: 50,
        width: '70%',
        backgroundColor: 'lightblue',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 5,
        marginTop: 20,
      }} onPress={pause}>
        <Text style={{
          fontSize: 22,
          fontWeight: '500'
        }}>Stop</Text>
      </TouchableOpacity>
      <TouchableOpacity style={{
        height: 50,
        width: '70%',
        backgroundColor: 'lightblue',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 5,
        marginTop: 20,
      }}>
        <Text style={{
          fontSize: 22,
          fontWeight: '500'
        }} onPress={handleResetClick}>Reset</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
