import React, { useEffect, useState } from 'react';
import { TouchableOpacity } from 'react-native';
import { StyleSheet, Text, View } from 'react-native';
import CountDown from 'react-native-countdown-component';
import clock from "./countdown"
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { FlatList } from 'react-native-gesture-handler';

export default function PlayACourse({ route, navigation }) {
  const { postureData } = route.params
  // const timeConvert = (time) => {
  //   const min = Math.floor(time / 60)
  //   const sec = time % 60
  // }
  const Stack = createNativeStackNavigator();
  const [min, setMin] = useState(postureData);
  const [sec, setSec] = useState(postureData[0].sec);
  const [isActive, setIsActive] = useState(true);

  useEffect(() => {
    let mySec = null
    if (isActive) {
      mySec = setTimeout(() => {
        setSec((sec) => sec - 1)
      }, 1000)
    }
    else if (!isActive) {
      clearInterval(mySec)
    }
    if (isActive == "reset") {
      setMin(0)
      setSec(0)
      clearInterval(mySec)
      setIsActive(true)
    }
  })
  const setActive = () => {
    console.log(isActive)
    if (isActive) {
      setIsActive(false)
    } 
    else if(!isActive){
      setIsActive(true)
    }
    else{
      setIsActive(true)
    }
  }
  const toReset = () => {
    setIsActive("reset")
  }

  return (
    <View style={styles.container}>
      <Text style={{ fontSize: 40, fontWeight: '700' }}>{min < 10 ? '0' : ''}{sec > 59 ? setMin(min + 1) : min} : {sec < 10 ? '0' : ''}{sec > 59 ? setSec(0) : sec}</Text>
      {/* <Stack.Navigator initialRouteName="My Courses" screenOptions={{
        headerShown: false
      }}>
        <Stack.Screen name="My Courses" component={clock} />
      </Stack.Navigator> */}
      <TouchableOpacity style={{
        height: 50,
        width: '70%',
        backgroundColor: 'lightblue',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 5,
        marginTop: 20,
      }} onPress={setActive}>
        <Text style={{
          fontSize: 22,
          fontWeight: '500'
        }}>Stop</Text>
      </TouchableOpacity>
      <TouchableOpacity style={{
        height: 50,
        width: '70%',
        backgroundColor: 'lightblue',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 5,
        marginTop: 20,
      }}>
        <Text style={{
          fontSize: 22,
          fontWeight: '500'
        }} onPress={toReset}>Reset</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
