import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, TextInput } from 'react-native';
import { useState } from 'react';

export default function HealthCalculatorScreen({ route, navigation }) {
  const [height, setHeight] = useState("")
  const [weight, setWeight] = useState("")
  return (
    <View style={styles.container}>
      <View style={{
        width: '100%',
        padding: 10,
      }}>
        <Text>Height(kg.)</Text>
        <TextInput
          style={styles.heightAndWeight}
          onChangeText={setHeight}
          value={height}
        />
        <Text>Weight(cm.)</Text>
        <TextInput
          style={styles.heightAndWeight}
          onChangeText={setWeight}
          value={weight}
        />
        <TouchableOpacity
               style = {styles.submitButton}
               onPress = {
                  () => this.calculate(this.state.height, this.state.weight)
               }>
               <Text style = {styles.submitButtonText}> Calculate </Text>
            </TouchableOpacity>
<Text style = {styles.output}>{this.state.bmi}</Text>
            <Text style = {styles.resultText}>{this.state.BmiResult}</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: "center",
    padding: 30,
    width: '100%'
  },
  heightAndWeight: {
    height: 40,
    width: "100%",
    borderWidth: 0.5,
    borderColor: "lightgray",
    borderRadius: 3,
    padding: 10,
    marginTop: 10
  },
});
