import React, { useState } from "react"
import { View, Text, TextInput, TouchableOpacity } from "react-native"
export const LoginScreen = ({navigation}) => {
    const [incorrect, setIncorrect] = useState(false)
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    // const validateLogin = () => {
    //     if()
    // }
    return (
        <View style={{
            flex : 1,
            backgroundColor : '#69DFFF'
        }}>
            <View style={{marginLeft : 20, marginTop : 20}}>
                <Text style={{
                    fontSize : 48,
                    fontWeight : '600',
                    color : 'white'
                }}>Sign In Account</Text>
            </View>
            <View style={{
                width : '100%',
                height : '80%',
                backgroundColor : 'white',
                borderRadius : 20,
                position : 'absolute',
                bottom : 0,
                padding : 20
            }}>
                {incorrect ? 
                    <View style={{
                        width : '100%',
                        height : 50,
                        borderWidth : 1,
                        borderColor : 'red',
                        backgroundColor : '#FFC0C0',
                        justifyContent : 'center',
                        paddingLeft : 20,
                        marginBottom : 20
                    }}>
                        <Text style={{color : 'black'}}>Your username or password is incorrect.</Text>
                    </View>
                    :
                    ""
                }
                <View>
                    <Text style={{marginBottom : 10, fontWeight : '600'}}>Username</Text>
                    <TextInput
                    onChangeText={setUsername}
                    value={username}
                    style={{
                        width : '100%',
                        height : 50,
                        borderWidth : 1,
                        borderColor : 'lightgray',
                        paddingLeft : 10,
                        borderRadius : 5
                    }}/>
                </View>

                <View style={{marginTop : 20}}>
                    <Text style={{marginBottom : 10, fontWeight : '600'}}>Password</Text>
                    <TextInput secureTextEntry={true}
                    onChangeText={setPassword}
                    value={password}
                    style={{
                        width : '100%',
                        height : 50,
                        borderWidth : 1,
                        borderColor : 'lightgray',
                        paddingLeft : 10,
                        borderRadius : 5
                    }}>
                    </TextInput>
                </View>

                <View style={{marginTop : 20}}>
                    <TouchableOpacity style={{
                        width : '100%',
                        height : 50,
                        backgroundColor : 'lightgreen',
                        borderRadius : 5,
                        justifyContent : 'center', 
                        alignItems : 'center'
                    }}>
                        <Text style={{fontWeight : '500', fontSize : 18, color : 'white'}}>Login</Text>
                    </TouchableOpacity>
                </View>
                
                <View style={{flexDirection : 'row', marginTop : 10, width : '100%', justifyContent : 'center'}}>
                    <Text style={{color : '#757575', fontWeight : '600'}}>If you haven't already an account ? </Text>
                    <TouchableOpacity style={{borderBottomWidth : 1, borderBottomColor : 'blue'}} onPress={() => {validateLogin()}}>
                        <Text style={{color : 'blue',fontWeight : '600'}}>Sign Up</Text>
                    </TouchableOpacity>
                </View>
            </View>
        </View>
    )
}
