import { createSlice } from "@reduxjs/toolkit";

const initailState = {
    user : null
}

export const userSlice = createSlice({
    name : 'user',
    initailState,
    reducers : {
        userdata : (state, action) => {
            state.user = "Hi"
        }
    }
})

export const { userdata } = userSlice.actions
export default userSlice.reducer
